mapper.select:
SELECT CONTRACT_DATE AS contractDate, BUSINESS_REG_NUMBER AS businessRegNumber, BUSINESS_NAME AS businessName, CONTRACT_MANAGER AS contractManager, SALES_MANAGER AS salesManager FROM CONTRACT
WHERE CONTRACT_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y%m%d') AND DATE_FORMAT(#{endDate}, '%Y%m%d')
ORDER BY CONTRACT_DATE DESC LIMIT #{pageSize} OFFSET #{firstIndex}

SELECT CONTRACT_DATE AS contractDate, BUSINESS_REG_NUMBER AS businessRegNumber, BUSINESS_NAME AS businessName, CONTRACT_MANAGER AS contractManager, SALES_MANAGER AS salesManager FROM CONTRACT
WHERE ${searchOption} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY CONTRACT_DATE DESC LIMIT #{pageSize} OFFSET #{firstIndex}

SELECT MID
FROM CONTRACT
WHERE MID LIKE #{mid}

mapper.update:
UPDATE CONTRACT
SET MID = #{mid}
WHERE BUSINESS_REG_NUMBER LIKE #{businessRegNumber}
